package TestPackage;

public class stringFirstAndLastIndex {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String string1 = "Anagnorisis";
		
		int firstIndex = string1.indexOf('s');
		System.out.println("first index of 's' is "+firstIndex);
		
		int lastIndex = string1.lastIndexOf('s');
		System.out.println("Last Index of 's' is "+lastIndex);
	}

}
