package javaAssignment3Part2;

public class EvenNumberReverseOrder {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("Printing even numbers in reverse order from 100 to 1 ");
		for (int number = 100; number >= 1; number -= 2) {
			System.out.println(number);
		}
	}

}
